   213â†’let inline makeTestList listName caseName corr prop cases = 
   214â†’    let getX x = ( ^a : (member X : ^t[]) x)
   215â†’    let getY x = ( ^a : (member Y : ^t[]) x)
   216â†’    cases
   217â†’    |> List.mapi
   218â†’        (fun i x ->
   219â†’            let i = i + 1
   220â†’            [
   221â†’                testCase $"{caseName} Case {i}" <| fun () -> 
   222â†’                    let corr = corr (getX x) (getY x)
   223â†’                    if Double.IsNaN (prop x) then
   224â†’                        Expect.isTrue (Double.IsNaN corr) "Should be equal (double precision)"
   225â†’                    else
   226â†’                        Expect.floatClose Accuracy.high corr (prop x) "Should be equal (double precision)"
   227â†’            ]
   228â†’        )
   229â†’    |> List.concat
   230â†’    |> testList $"Correlation.Seq.{listName}"
