rage < 50% and substantial size\nprint(\"FSharp.Stats modules with < 50% coverage (>50 lines):\\n\")\ncount = 0\nfor mod in modules:\n    if mod['pct'] < 50 and mod['total'] > 50:\n        count += 1\n        print(f\"{count}. {mod['filename']}\")\n        print(f\"   Coverage: {mod['covered']}/{mod['total']} lines ({mod['pct']:.1f}%)\")\n        print()\n        if count >= 25:\n            break\n\nEOF\n","description":"Analyze coverage by file"}}],"stop_reason":null,"stop_sequence":null,"usage":{"input_tokens":7,"cache_creation_input_tokens":0,"cache_read_input_tokens":52270,"cache_creation":{"ephemeral_5m_input_tokens":0,"ephemeral_1h_input_tokens":0},"output_tokens":1,"service_tier":"standard"}},"parent_tool_use_id":null,"session_id":"7b338836-d59e-4880-ac0d-e697ed099275","uuid":"e20d2ab9-1de0-4883-8b01-1158213168c1"}
